package com.ecquaria.cloud.moh.iais.tags;

import com.ecquaria.cloud.moh.iais.dto.SearchParam;
import com.ecquaria.cloud.moh.iais.dto.SearchResult;
import org.junit.Before;
import org.junit.Test;
import org.junit.runner.RunWith;
import org.mockito.Mock;
import org.mockito.Spy;
import org.powermock.api.mockito.PowerMockito;
import org.powermock.api.mockito.mockpolicies.Slf4jMockPolicy;
import org.powermock.core.classloader.annotations.MockPolicy;
import org.powermock.core.classloader.annotations.PrepareForTest;
import org.powermock.modules.junit4.PowerMockRunner;
import org.powermock.reflect.Whitebox;
import org.springframework.mock.web.MockHttpServletRequest;

import javax.servlet.jsp.JspException;
import javax.servlet.jsp.JspTagException;
import javax.servlet.jsp.JspWriter;
import javax.servlet.jsp.PageContext;
import java.io.IOException;
import java.util.ArrayList;
import java.util.List;

import static org.junit.Assert.assertEquals;
import static org.mockito.Matchers.anyString;
import static org.powermock.api.mockito.PowerMockito.*;

/**
 * SortableHeaderTagTest
 *
 * @author suocheng
 * @date 8/6/2019
 */
@RunWith(PowerMockRunner.class)
@MockPolicy(Slf4jMockPolicy.class)
@PrepareForTest({SortableHeaderTag.class})
public class SortableHeaderTagTest {
    @Spy
    private SortableHeaderTag tag = new SortableHeaderTag();
    MockHttpServletRequest request = new MockHttpServletRequest();
    @Mock
    private JspWriter jw;

    @Before
    public void setup() throws IOException {
        PageContext pageContext = PowerMockito.mock(PageContext.class);
        Whitebox.setInternalState(tag, "pageContext", pageContext);
        when(pageContext.getRequest()).thenReturn(request);
        when(pageContext.getOut()).thenReturn(jw);
        doNothing().when(jw).print(anyString());
        tag.setId("idd");
        tag.setStyle("ssss");
        tag.setField("field");
        tag.setValue("value");
        tag.setParam(null);
        tag.setJsFunc(null);
        tag.setNeedSort(true);
        SearchResult<String> sr = new SearchResult<String>();
        List<String> list = new ArrayList<>();
        list.add("aaa");
        list.add("bbb");
        list.add("ccc");
        sr.setRows(list);
        sr.setRowCount(list.size());
        request.setAttribute("searchResult", sr);
    }

    @Test
    public void testdoStartTagASC() throws JspException {
        SearchParam param = new SearchParam(String.class);
        param.setPageNo(2);
        param.setPageSize(1);
        param.addSort("field",SearchParam.ASCENDING);
        request.setAttribute("searchParam", param);
        int ret = tag.doStartTag();
        assertEquals(SortableHeaderTag.SKIP_BODY, ret);
    }
    @Test
    public void testdoStartTagDESC() throws JspException {
        SearchParam param = new SearchParam(String.class);
        param.setPageNo(2);
        param.setPageSize(1);
        param.addSort("field",SearchParam.DESCENDING);
        request.setAttribute("searchParam", param);
        int ret = tag.doStartTag();
        assertEquals(SortableHeaderTag.SKIP_BODY, ret);
    }
    @Test(expected = JspTagException.class)
    public void testDoStartTagExp() throws JspException, IOException {
        doThrow(new RuntimeException()).when(jw).print(anyString());
        int ret = tag.doStartTag();
    }
    @Test
    public void testDoEndTag() throws JspException {
        int ret = tag.doEndTag();
        assertEquals(SortableHeaderTag.EVAL_PAGE, ret);
        tag.release();
    }


}
